mapper.select:
SELECT t.social_code, t.social_name, v.nickname, v.headimgurl, v.binding_date, CASE v.binding WHEN 1 THEN 1 ELSE 0 END AS binding FROM ( SELECT 1 AS social_code, '微信' AS social_name UNION ALL SELECT 2 AS social_code, '码云' AS social_name UNION ALL SELECT 3 AS social_code, 'QQ' AS social_name ) t LEFT JOIN ( SELECT 1 AS social_code, '微信' AS social_name, wowu.nickname AS nickname, wowu.headimgurl AS headimgurl, wowu.binding_date AS binding_date, 1 AS binding FROM wx_open_website_users wowu WHERE wowu.users_id = #{usersId}
UNION ALL
SELECT 2 AS social_code, '码云' AS social_name, gu.`name` AS nickname, gu.avatar_url AS headimgurl, gu.binding_date AS binding_date, 1 AS binding FROM gitee_users gu WHERE gu.users_id = #{usersId}
UNION ALL
SELECT 3 AS social_code, 'QQ' AS social_name, qwu.nickname AS nickname, qwu.figureurl_2 AS headimgurl, qwu.binding_date AS binding_date, 1 AS binding FROM qq_website_users qwu WHERE qwu.users_id = #{usersId} ) v ON v.social_code = t.social_code
